mapper.select:
select
id, spaceName, spaceLevel, maxSize, maxCount, totalSize, totalCount, userId, createTime, editTime, updateTime,
isDelete, spaceType
from space


        and id = #{id}


        and spaceName = #{spaceName}


        and spaceLevel = #{spaceLevel}


        and maxSize = #{maxSize}


        and maxCount = #{maxCount}


        and totalSize = #{totalSize}


        and totalCount = #{totalCount}


        and userId = #{userId}


        and createTime = #{createTime}


        and editTime = #{editTime}


        and updateTime = #{updateTime}


        and isDelete = #{idDelete}


        and spaceType = #{spaceType}


limit #{pageable.offset}, #{pageable.pageSize}

select Count(1)
from space


        and id = #{id}


        and spaceName = #{spaceName}


        and spaceLevel = #{spaceLevel}


        and maxSize = #{maxSize}


        and maxCount = #{maxCount}


        and totalSize = #{totalSize}


        and totalCount = #{totalCount}


        and userId = #{userId}


        and createTime = #{createTime}


        and editTime = #{editTime}


        and updateTime = #{updateTime}


        and isDelete = #{idDelete}


        and spaceType = #{spaceType}
