mapper.select:
        select
id, spaceId, userId, spaceRole, createTime, updateTime
        from space_user


                and id = #{id}


                and spaceId = #{spaceId}


                and userId = #{userId}


                and spaceRole = #{spaceRole}


                and createTime = #{createTime}


                and updateTime = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from space_user


        and id = #{id}


        and spaceId = #{spaceId}


        and userId = #{userId}


        and spaceRole = #{spaceRole}


        and createTime = #{createTime}


        and updateTime = #{updateTime}
